# 读取mashup.json文件
with open("Original Dataset/mashups.json", "r", encoding="utf-8") as f:
    mashups = json.load(f)

# 分成两组
matched_and_no_api = []
others = []

# 遍历每个mashup
for mashup in mashups:
    mashup_id = mashup.get("id")
    description = mashup.get("description", "").lower()
    if mashup_id in my_list and "api" not in description:
        matched_and_no_api.append(mashup)
    else:
        others.append(mashup)

# 合并为新的排序
reordered_mashups = matched_and_no_api + others

# 输出统计信息
print(f"符合条件（在列表里且description不包含'API'）的 mashup 数量: {len(matched_and_no_api)}")

# （可选）将重新排序的结果写回文件
with open("Original Dataset/mashup_reordered.json", "w", encoding="utf-8") as f:
    json.dump(reordered_mashups, f, indent=2, ensure_ascii=False)
